mapper.select:
SELECT
-- 级联表的字段 ,tushu.tushu_bianhao as tushuBianhao ,tushu.tushu_name as tushuName ,tushu.tushu_zuozhe as tushuZuozhe ,tushu.tushu_photo as tushuPhoto ,tushu.tushu_types as tushuTypes ,tushu.chubanshe_types as chubansheTypes ,tushu.tushu_kuchuen as tushuKuchuen ,tushu.tushu_jieyue as tushuJieyue ,tushu.tushu_content as tushuContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_xuehao as yonghuXuehao ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_zhuzhi as yonghuZhuzhi ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_xueyuan as yonghuXueyuan ,yonghu.yonghu_zhanye as yonghuZhanye ,yonghu.yonghu_banji as yonghuBanji
FROM tushu_liuyan a left JOIN tushu tushu ON a.tushu_id = tushu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.tushu_id = #{params.tushuId}
and a.yonghu_id = #{params.yonghuId}
and a.tushu_liuyan_content like CONCAT('%',#{params.tushuLiuyanContent},'%')
and a.reply_content like CONCAT('%',#{params.replyContent},'%')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and tushu.tushu_bianhao like CONCAT('%',#{params.tushuBianhao},'%')
and tushu.tushu_name like CONCAT('%',#{params.tushuName},'%')
and tushu.tushu_zuozhe like CONCAT('%',#{params.tushuZuozhe},'%')
and tushu.tushu_types = #{params.tushuTypes}
and tushu.chubanshe_types = #{params.chubansheTypes}
and tushu.tushu_kuchuen >= #{params.tushuKuchuenStart}
and tushu.tushu_kuchuen <= #{params.tushuKuchuenEnd}
and tushu.tushu_jieyue >= #{params.tushuJieyueStart}
and tushu.tushu_jieyue <= #{params.tushuJieyueEnd}
and tushu.tushu_content like CONCAT('%',#{params.tushuContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_xuehao like CONCAT('%',#{params.yonghuXuehao},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_zhuzhi like CONCAT('%',#{params.yonghuZhuzhi},'%')
and yonghu.yonghu_xueyuan like CONCAT('%',#{params.yonghuXueyuan},'%')
and yonghu.yonghu_zhanye like CONCAT('%',#{params.yonghuZhanye},'%')
and yonghu.yonghu_banji like CONCAT('%',#{params.yonghuBanji},'%')
order by a.${params.orderBy} desc
